import React, { Component } from 'react';
import './App.css';
import List from './list.js'

class App extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = { todos:["start"] ,
                   input: ""}
  } 
  add = (e) => {
    e.preventDefault();
    const copy = this.state.todos.slice();
    copy.push("newToDo")
    this.setState({todos: copy})
  }

  change = (e) => {
    e.preventDefault();
      console.log(input);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>
           To-Do-List
          </p>
        </header>
        <input className= "addTodo" id="text" type="text" name="addTodo" placeholder="" onClick="{this.change}"></input>
        <input className= "addTodo"id="form-submit" type="submit" value="Add" onClick={this.add} ></input>
        <List todos={this.state.todos} add={this.add} input={this.state.input} change={this.change} >
        </List>
      </div>
    );
  }
}

export default App;
