import React, { Component } from 'react';
import './App.css';
import List from './items.js'

class App extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = { todos:[],
                   input:""}
  } 
  add = (e) => {
    e.preventDefault();
    const copy = this.state.todos.slice();
    copy.push(this.state.input)
    this.setState({todos: copy, input: ""})
  }

  change = (e) => {
    e.preventDefault();
    this.setState({
      input: e.target.value
    })

  }

  render() {
    let items = this.state.todos.map((ele, i)=>{
    return <ul id={i}>{ele}</ul>
    })
    return (
      <div className="App">
        <header className="App-header">
          <p>
           To-Do-List
          </p>
        </header>
        <input className= "addTodo" id="text" type="text" name="addTodo" placeholder="" onChange={this.change}></input>
        <input className= "addTodo"id="form-submit" type="submit" value="Add" onClick={this.add} ></input>
        <br></br>
        {this.state.todos.length === 0 ? "No todos yet!" : "You still have todos"}
        {items}
      </div>
    );
  }
}

export default App;
